mapper.select:
select feed_record_id from feed_record


        batch_id = #{batchId}


        OR feed_id = #{feedId}


        OR feed_person = #{feedPerson}


        OR record_person = #{recordPerson}

select *
from feed_record
where record_time between #{startTime} and #{endTime}

select fr.feed_record_id, fr.batch_id, fr.feed_id, fr.feed_amount, fr.feed_person, fr.record_person, fr.record_time, fr.gmt_create, fr.gmt_modified, bi.batch_name, fi.feed_name, ubi.name as recordPersonName, ubi2.name as feedPersonName from feed_record fr join batch_info bi on fr.batch_id = bi.batch_id join feed_info fi on fr.feed_id = fi.feed_id join user_basic_info ubi on ubi.user_basic_info_id = fr.record_person join user_basic_info ubi2 on ubi2.user_basic_info_id = fr.feed_person
fr.record_person = #{recordPerson}
AND fr.feed_person = #{feedPerson}
AND fr.batch_id = #{batchId}
AND fr.record_time BETWEEN #{startDate} AND #{endDate}
