mapper.select:
select fertilization_record_id from fertilization_record


        batch_id = #{batchId}


        OR operate_person = #{operatePerson}


        OR record_person = #{recordPerson}

select fr.fertilization_record_id, fr.batch_id, fr.fertilization_rate, fr.fertilization_time, fr.operate_person, fr.record_person, fr.record_time, fr.gmt_create, fr.gmt_modified, bi.batch_name, ubi.name as recordPersonName, ubi2.name as operatePersonName from fertilization_record fr join batch_info bi on fr.batch_id = bi.batch_id join user_basic_info ubi on ubi.user_basic_info_id = fr.record_person join user_basic_info ubi2 on ubi2.user_basic_info_id = fr.operate_person
fr.record_person = #{recordPerson}
AND fr.operate_person = #{operatePerson}
AND fr.batch_id = #{batchId}
AND fr.record_time BETWEEN #{startDate} AND #{endDate}
AND fr.fertilization_time BETWEEN #{fertilizationStartDate} AND #{fertilizationEndDate}
